                scimoz.setMarginTypeN(scimoz.MARGIN_SYMBOLS, scimoz.SC_MARGIN_SYMBOL);
                scimoz.setMarginMaskN(scimoz.MARGIN_SYMBOLS, ko.markers.MARKERS_MASK_SYMBOLS);
                scimoz.setMarginSensitiveN(scimoz.MARGIN_SYMBOLS, 1);

                // Line numbers margin.
