                // Tracking margin.
                scimoz.setMarginTypeN(scimoz.MARGIN_TRACKING, scimoz.SC_MARGIN_TEXT);
                scimoz.setMarginMaskN(scimoz.MARGIN_TRACKING, 0);
                scimoz.setMarginSensitiveN(scimoz.MARGIN_TRACKING, 1);

