    if (!doNotOfferToSave && isLastDocumentInstance &&
        this.koDoc.isDirty) {
        var res = ko.dialogs.yesNoCancel("Save changes to " +
                                        this.koDoc.baseName +
                                        "?");
        if (res == "Cancel") {
            return false;
