                    getService(Components.interfaces.koIViewService);
    try {
        viewSvc.registerView(v);
    } catch (ex) {
        this.log.error("Unable to register view: " + v);
    }

    if (previous && previous.getAttribute("type") == "quickstart")
        previous.close();

    return v;
} catch(e) {
    this.log.exception(e);
